import com.eviware.soapui.support.XmlHolder
//groovy script to capture the response and fetch the session id stre it in testcare/test suit level
//creating the veriabal called response which will store the response
def response = context.testCase.getTestStepByName('create customer').getPropertyValue('Response')
log.info"the Response is = $response"
def holder1 = new XmlHolder(response)
def cid = holder1.getNodeValue("//*:id")
//log.info sess
context.testCase.testSuite.setPropertyValue("id",cid)
context.testCase.setPropertyValue("id",cid)
context.testCase.testSuite.project.setPropertyValue("id",cid)

